    private void Button_Clicked(object sender, EventArgs e)
    {
      string operationChars = "+-/X=";
      Button button = (Button)sender;
      if (operationChars.Contains(button.Text))
      {
        Output.Text = string.Empty;
      }
      else
      {
        Output.Text += button.Text;
      }
    } 
