        (decimal)value < (decimal)minimumValue) 
      { 
        string[] memberNames =  
          new string[] { validationContext.MemberName }; 
        return new ValidationResult(ErrorMessage, memberNames); 
      } 
      return ValidationResult.Success; 
    } 
