    // UIManager.cs
    using UnityEngine;
    using TMPro;
    public class UIManager : MonoBehaviour
    {
        public TextMeshProUGUI scoreText;
        private int score = 0;
        void Start()
        {
            UpdateScoreText();
        }
        public void IncrementScore(int amount)
        {
            score += amount;
            UpdateScoreText();
        }
        void UpdateScoreText()
        {
            scoreText.text = "Score: " + score.ToString();
        }
    }
