private void OnBoldButtonClicked(object sender,  
 RoutedEventArgs e) 
{ 
    txtBox.FontWeight =  
        txtBox.FontWeight == FontWeights.Bold ? 
        FontWeights.Normal : FontWeights.Bold; 
} 
